#define DONOTHING() do {} while (0)

/* Find basic rate for a given rate */
#define WLC_BASIC_RATE(wlc, rspec)	((wlc)->band->basic_rate[RSPEC_REFERENCE_RATE(rspec)])

#define PHY_TXC_FRAMETYPE(r)	(RSPEC_ISVHT(r) ?			\
				 FT_VHT :				\
				 (RSPEC_ISHT(r) ?			\
				  FT_HT :				\
				  (IS_CCK(r) ? FT_CCK : FT_OFDM)))

#define D11N_MMPLCPLen(rxs)	((rxs)->PhyRxStatus_3 & PRXS3_nphy_MMPLCPLen_MASK)
#define D11HT_MMPLCPLen(rxs)	((((rxs)->PhyRxStatus_1 & PRXS1_HTPHY_MMPLCPLenL_MASK) >> 8) | \
				(((rxs)->PhyRxStatus_2 & PRXS2_HTPHY_MMPLCPLenH_MASK) << 8))
#define RFDISABLE_DEFAULT	10000000 /* rfdisable delay timer 500 ms, runs of ALP clock */
#define	SW_TIMER_LINK_REPORT	10

/* Get the bw, in required BW_XXMHZ encoding, from chanspec */
#define WLC_GET_BW_FROM_CHSPEC(cs) \
	(CHSPEC_IS80(cs) ? BW_80MHZ : CHSPEC_IS40(cs) ? BW_40MHZ : BW_20MHZ)

