static void
wlc_get_mcsset(wlc_info_t *wlc, void *arg, bool istx)
{
	uint8 streams = istx ? wlc->stf->txstreams : wlc->stf->rxstreams;
	uint8 *mcs = (uint8*)arg;
	int i;

	memset(mcs, 0, MCSSET_LEN);
	for (i = 0; i < streams; i++)
		mcs[i] = 0xff;
}
