	uint32 nv_offset_2g_20M[] = {20, 32, 29, 25, 21, 13, 5, 5, 20, 20, 16, 16, 5, 5, 5, 5};
	uint32 nv_offset_2g_40M[] = {20, 32, 29, 25, 21, 13, 5, 5, 20, 20, 16, 16, 5, 5, 5, 1};
